                  VALUES('$nombre', '$codigo', '$cantidad', '$precio', '$marca', '$cont_neto', '$fecha', '$imagen_ref')";
        $resultado = mysqli_query($conn, $query);
        verificar_resultado( $resultado );
        cerrar($conn);
