    function modificar($id, $nombre, $codigo, $cantidad, $precio, $marca, $cont_neto, $fecha, $conn){

        $query="UPDATE id18070131_sivenpro.productos SET
                       nombre_producto= '$nombre',
                       cod_producto = '$codigo',
                       cantidad = '$cantidad',
                       precio = '$precio',
                       marca ='$marca',
                       cont_neto = '$cont_neto',
                       fecha_venc= '$fecha'
                WHERE id_producto='$id'";
                // echo $query;
        $resultado = mysqli_query($conn, $query);
        verificar_resultado($resultado);
        cerrar( $conn );
    }


    function eliminar($id, $conn){
        $query="UPDATE id18070131_sivenpro.productos SET estado='Eliminado' WHERE id_producto='$id'";
        $resultado = mysqli_query($conn, $query);
