    function verificar_resultado($resultado){
        if (!$resultado) $informacion["respuesta"] = "ERROR";
        else $informacion["respuesta"] = "BIEN";
        echo json_encode($informacion);
    }


    function existe_producto($nombre, $codigo, $conn){
        $query = "SELECT * FROM id18070131_sivenpro.productos 
                 WHERE nombre_producto='$nombre' AND cod_producto='$codigo' AND estado='Activo'";
        $resultado = mysqli_query($conn, $query);
        $existe_producto = mysqli_num_rows( $resultado );
        return $existe_producto;
    }


    function consultaCantidadProducto($id, $conn){
        $arreglo = array();
        $consulta = "SELECT cantidad FROM id18070131_sivenpro.productos WHERE id_producto='$id'";
        $ejecuta = mysqli_query($conn,$consulta);
        while ($resultado = mysqli_fetch_assoc($ejecuta)){
            if($resultado["cantidad"] != null || $resultado["cantidad"] != ""){
                array_push($arreglo, $resultado["cantidad"]);
            }
        }
        return $arreglo;
    }


    function consultaPrecioProducto($id, $conn){
        $arreglo = array();
        $consulta = "SELECT precio FROM id18070131_sivenpro.productos WHERE id_producto='$id'";
        $ejecuta = mysqli_query($conn,$consulta);
        while ($resultado = mysqli_fetch_assoc($ejecuta)){
            if($resultado["precio"] != null || $resultado["precio"] != ""){
                array_push($arreglo, $resultado["precio"]);
            }
        }
        return $arreglo;
    }


    function cerrar( $conn ){
