<?php

    session_start();
    include("../../../conexion/config/connection.php");
    mysqli_set_charset($conn, "utf8");
    
    $query="SELECT a.*, DATE_FORMAT(a.fecha_venc, '%d/%m/%Y') AS fecha_vencimiento, DATE_FORMAT(b.fecha_insertado, '%d/%m/%Y') AS fecha_notif, DATE_FORMAT(b.fecha_insertado, '%H:%i:%s %p') AS hora_notif, b.id_notificacion, b.estado_notificacion
            FROM id18070131_sivenpro.productos a
            INNER JOIN id18070131_sivenpro.notificaciones b ON b.id_producto = a.id_producto
            WHERE a.estado = 'Activo'
            AND b.estado_notificacion = 'Activo'
            ORDER BY fecha_notif ASC, hora_notif ASC";

    $cantidad_datos=0;
    $resultado = mysqli_query($conn, $query);

    if(!$resultado){ die("Error en la consulta!");
    }else{
        while ($data = mysqli_fetch_assoc($resultado)){
          $cantidad_datos++;

          $id = $data['id_notificacion'];

          if($data['imagen_ref']=="" || $data['imagen_ref']==null){
            $imagen= "";
          }else{
            $imagen= $data['imagen_ref'];
          }


          echo $texto =
            '<div class="modal-body">
              <div class="row">
                <div class="col-3">
                  <p class="text-center">
                    <img class="foto-notificacion" src="data:image/png;base64,'.$imagen.'">
                  </p>
                </div>
                
                <div class="col-9">
                  <p>El producto <span class="badge badge-pill badge-info">'.$data["nombre_producto"].'</span>,
                  código <span class="badge badge-pill badge-warning">'.$data["cod_producto"].'</span>
                  está pronto a cumplir la <strong>fecha de vencimiento</strong> establecida para el <span class="break"><strong>'.$data["fecha_vencimiento"].'</span></strong>.
                  <p>
                    <strong>¡Se recomienda tomar acciones preventivas!</strong>
                    <br>PRECIO REFERENCIAL: <strong>$'.$data["precio"].'</strong>
                  </p>
                  <h6>
                    <i class="ti-time"></i> Recibido el '.$data["fecha_notif"].' a las '.$data["hora_notif"].'    
                    <button type="button" rel="tooltip" class="btn btn-danger dlt_notifa" style="margin: 0 !important;padding: 7px !important; float:right;" title="Descartar" onclick="descartarNotificacion('.$id.')">
                      <i class="material-icons">delete</i>
                    </button>
                  </h6>
                </div>
              </div>
            </div>
            <hr size="2px" width="100%" noshade="noshade" align="right" />';
        }
        

        if ($cantidad_datos==0) {
          echo $texto =
          '<li">
            <div style="text-align:center;">
              <h4><strong>Actualmente no posee notificaciones...</strong></h4>
              <p>
                <img src="https://9044c4.medialib.edu.glogster.com/VAzsr0mPpG8jtnLal4Di/media/a7/a7cb9087ae050db7d838fdbdb126ddac75f7e3df/wp42cbc27e.gif" alt="this slowpoke moves" width="250"/>
              </p>
            </div>
          </li>';
        }

        //echo json_encode($arreglo);
        mysqli_free_result($resultado);
        mysqli_close($conn);
    }

?>